import { useEffect, useState, useRef } from "react";

export default function useKrakenPrices(pairs) {
    const [prices, setPrices] = useState({});
    const wsRef = useRef(null);
    const reconnectTimeoutRef = useRef(null);
    const heartbeatIntervalRef = useRef(null);

    useEffect(() => {
        const connect = () => {
            const ws = new WebSocket("wss://ws.kraken.com/v2");
            wsRef.current = ws;

            ws.onopen = () => {
                console.log("âœ… Connected to Kraken");

                // ÐÐ±Ð¾Ð½Ð¸Ñ€Ð°Ð¼Ðµ ÑÐµ Ð·Ð° Ð¸Ð·Ð±Ñ€Ð°Ð½Ð¸Ñ‚Ðµ ÑÐ¸Ð¼Ð²Ð¾Ð»Ð¸
                ws.send(JSON.stringify({
                    method: "subscribe",
                    params: {
                        channel: "ticker",
                        symbol: pairs
                    }
                }));

                // Ð˜Ð·Ð¿Ñ€Ð°Ñ‰Ð°Ð¼Ðµ ping Ð½Ð° Ð²ÑÐµÐºÐ¸ 20 ÑÐµÐº.
                heartbeatIntervalRef.current = setInterval(() => {
                    if (ws.readyState === WebSocket.OPEN) {
                        ws.send(JSON.stringify({ method: "ping" }));
                    }
                }, 20000);
            };

            ws.onmessage = (event) => {
                try {
                    const msg = JSON.parse(event.data);
                    if (msg.channel === "ticker" && (msg.type === "snapshot" || msg.type === "update")) {
                        msg.data.forEach(ticker => {
                            setPrices(prev => ({
                                ...prev,
                                [ticker.symbol]: ticker.last
                            }));
                        });
                    }
                } catch (err) {
                    console.error("Parse error:", err);
                }
            };

            ws.onerror = () => {
                console.error("âš ï¸ WebSocket error, closing...");
                ws.close();
            };

            ws.onclose = () => {
                console.log("ðŸ”„ Reconnecting in 3 seconds...");
                clearInterval(heartbeatIntervalRef.current);
                reconnectTimeoutRef.current = setTimeout(connect, 3000);
            };
        };

        connect();

        // Cleanup Ð¿Ñ€Ð¸ unmount
        return () => {
            console.log("ðŸ›‘ Cleaning up WebSocket");
            clearTimeout(reconnectTimeoutRef.current);
            clearInterval(heartbeatIntervalRef.current);
            if (wsRef.current) {
                wsRef.current.close();
            }
        };
    }, [pairs]);

    return prices;
}
