import { useEffect, useState } from "react";

export default function useKrakenPrices(pairs) {
    const [prices, setPrices] = useState({});

    useEffect(() => {
        if (!pairs || pairs.length === 0) return;

        // ÐŸÑ€ÐµÐ¼Ð°Ñ…Ð²Ð°Ð¼Ðµ Ð¿Ñ€Ð°Ð·Ð½Ð¸ Ð¸Ð»Ð¸ Ð½ÐµÐ¿Ð¾Ð´Ð´ÑŠÑ€Ð¶Ð°Ð½Ð¸ ÑÐ¸Ð¼Ð²Ð¾Ð»Ð¸
        const validPairs = pairs.filter(p => typeof p === "string" && p.includes("/"));
        if (validPairs.length === 0) return;

        const ws = new WebSocket("wss://ws.kraken.com/v2");

        ws.onopen = () => {
            console.log("âœ… Connected to Kraken v2");

            // ÐœÐ°Ð»ÐºÐ¾ Ð¸Ð·Ñ‡Ð°ÐºÐ²Ð°Ð½Ðµ Ð·Ð° ÑÑ‚Ð°Ð±Ð¸Ð»ÐµÐ½ subscribe
            setTimeout(() => {
                ws.send(JSON.stringify({
                    method: "subscribe",
                    params: {
                        channel: "ticker",
                        symbol: validPairs
                    }
                }));
                console.log("ðŸ“¡ Subscribed to:", validPairs);
            }, 150);
        };

        ws.onmessage = (event) => {
            try {
                const msg = JSON.parse(event.data);

                // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐ²Ð°Ð¼Ðµ Ð´Ð°Ð»Ð¸ Ðµ ticker snapshot Ð¸Ð»Ð¸ update
                if (msg.channel === "ticker" && (msg.type === "snapshot" || msg.type === "update")) {
                    msg.data.forEach(ticker => {
                        // Ð’Ð·Ð¸Ð¼Ð°Ð¼Ðµ Ð¿Ñ€Ð°Ð²Ð¸Ð»Ð½Ð¾ last Ñ†ÐµÐ½Ð° (Ð¼Ð¾Ð¶Ðµ Ð´Ð° Ðµ Ñ‡Ð¸ÑÐ»Ð¾ Ð¸Ð»Ð¸ Ð¼Ð°ÑÐ¸Ð²)
                        const lastPrice = Array.isArray(ticker.last) ? ticker.last[0] : ticker.last;
                        
                        setPrices(prev => ({
                            ...prev,
                            [ticker.symbol]: parseFloat(lastPrice).toFixed(2)
                        }));
                    });
                }

            } catch (err) {
                console.error("âŒ Error parsing Kraken message:", err);
            }
        };

        ws.onclose = () => console.log("ðŸ”Œ Disconnected from Kraken");

        return () => ws.close();
    }, [pairs]);

    return prices;
}
