import React, { useState, useEffect } from "react";
import useKrakenPrices from "../Cryptos/KrakenApi";
import useKrakenPricesRest from "../Cryptos/KrakenApiBugSolve";
import "./sellForm.css"
import "./style.css";

const Profile = ({ onLogout }) => {
    const [user, setUser] = useState(JSON.parse(localStorage.getItem("user")));
    const [holdings, setHoldings] = useState([]);
    const [history, setHistory] = useState([]);
    //const prices = useKrakenPrices(holdings.map(h => h.cryptoSymbol));
    const prices = useKrakenPricesRest(holdings.map(h => h.cryptoSymbol));
    const [isSellOpen, setIsSellOpen] = useState(false);
    const [selectedCrypto, setSelectedCrypto] = useState(null);
    const [sellQuantity, setSellQuantity] = useState("");
    const [totalSell, setTotalSell] = useState(0);

    const openSellForm = (crypto) => {
        setSelectedCrypto(crypto);
        setIsSellOpen(true);
    }
    const closeSellForm = () => {
        setSelectedCrypto(null);
        setIsSellOpen(false);
    }



    const fetchData = () => {
        fetch(`http://localhost:8080/api/trade/holdings/${user.id}`)
            .then(res => res.json())
            .then(data => setHoldings(data))
            .catch(err => console.error("Error fetching holdings:", err));

        fetch(`http://localhost:8080/api/trade/history/${user.id}`)
            .then(res => res.json())
            .then(data => setHistory(data))
            .catch(err => console.error("Error fetching history:", err));

    }

    useEffect(() => {
        fetchData();
    }, []);

    const refreshAll = async () => {
    try {
        // 1. Взимаме новия user
        const resUser = await fetch(`http://localhost:8080/api/users/${user.id}`);
        const updatedUser = await resUser.json();
        localStorage.setItem("user", JSON.stringify(updatedUser));
        setUser(updatedUser);

        // 2. Взимаме новите holdings
        const resHoldings = await fetch(`http://localhost:8080/api/trade/holdings/${user.id}`);
        setHoldings(await resHoldings.json());

        // 3. Взимаме новата история
        const resHistory = await fetch(`http://localhost:8080/api/trade/history/${user.id}`);
        setHistory(await resHistory.json());
    } catch (err) {
        console.error("Failed to refresh data", err);
    }
};


    const makeSell = () => {
        const qty = parseFloat(sellQuantity);
        if (isNaN(qty) || qty <= 0) {
            alert("Enter quantity");
            return;
        }

        const currentPrice = prices[selectedCrypto.cryptoSymbol];
        if (!currentPrice) {
            alert("No price available! Try again later.");
            return;
        }

        const symbol = encodeURIComponent(selectedCrypto.cryptoSymbol);
        fetch(`http://localhost:8080/api/trade/sell/${user.id}?cryptoSymbol=${symbol}&quantity=${qty}&price=${currentPrice}&cryptoName=${selectedCrypto.cryptoName}`, {
            method: "POST"
        })

            .then(async (res) => {
                const message = await res.text();
                if (!res.ok) {
                    throw new Error(message);
                }
                return message;
            })
            .then((msg) => {
                alert(msg);
                refreshAll();
                closeSellForm();
            })
            .catch((err) => {
                alert(err.message);
            });
    };





    return (
        <div className="allProfileInfo">
            <div className="profile">
                <div className="profileInfo">
                    <h1>{user?.name}</h1>
                    <p>{user?.email}</p>
                </div>
                <div className="balance"><h1>Balance: {user?.balance.toFixed(2)}</h1></div>
                <div className="profileButtons">
                    <button>Reset</button>
                    <button onClick={onLogout}>Logout</button>
                </div>
            </div>
            <div className="profileInfo2">
                <div className="boxLists">
                    <div className="headerLists"><h1>Holdings</h1></div>
                    <div className="infoLists">
                        {holdings.length > 0 ?
                            <table>
                                <thead>
                                    <tr>
                                        <th className="symbol">Symbol</th>
                                        <th className="cryptoName">Name</th>
                                        <th className="quantity">Quantity</th>
                                        <th className="sell"></th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {holdings.map((h, index) => (
                                        <tr key={index}>
                                            <td className="symbol">{h.cryptoSymbol}</td>
                                            <td className="cryptoName">{h.cryptoName}</td>
                                            <td className="quantity">{h.quantity}</td>
                                            <td className="sell"><button onClick={() => openSellForm(h)}>Sell</button></td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                            :
                            <p>No holdings yet</p>}
                    </div>
                </div>
                <div className="boxLists">
                    <div className="headerLists"><h1>History</h1></div>
                    <div className="infoLists">
                        {history.length > 0 ? (
                            <table>
                                <thead>
                                    <tr>
                                        <th>Symbol</th>
                                        <th>Name</th>
                                        <th>Type</th>
                                        <th>Quantity</th>
                                        <th>Time</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {history.map((h, index) => (
                                        <tr key={index}>
                                            <td className="symbol">{h.cryptoSymbol}</td>
                                            <td>{h.cryptoName}</td>
                                            <td>{h.type}</td>
                                            <td>{h.quantity}</td>
                                            <td className="time">{h.timePurchase}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        ) : (
                            <p>No transactions made yet</p>
                        )}
                    </div>
                </div>
                {isSellOpen === true ?

                    <div className="overlay">
                        <div className="form-container">
                            <h1>Sell {selectedCrypto.cryptoName}</h1>
                            <p>Symbol: {selectedCrypto.cryptoSymbol}</p>
                            <p>Available: {selectedCrypto.quantity}</p>
                            <p>Price: {prices[selectedCrypto.cryptoSymbol]}</p>
                            <p>Total: {totalSell.toFixed(2)}</p>
                            <input type="number" min="0" step="0.01" max={selectedCrypto.quantity}
                                placeholder="Quantity to sell" onChange={(e) => {
                                    const value = e.target.value;
                                    setSellQuantity(value);

                                    const qty = parseFloat(value);
                                    const price = prices[selectedCrypto.cryptoSymbol];
                                    if (!isNaN(qty) && qty > 0 && price) {
                                        setTotalSell(qty * price);
                                    } else {
                                        setTotalSell(0);
                                    }
                                }} />

                            <div className="buttons">
                                <button className="confirm" onClick={() => makeSell()}>Confirm</button>
                                <button className="cancel" onClick={() => closeSellForm()}>Cancel</button>
                            </div>
                        </div>
                    </div> : <></>};


            </div>

        </div>


    )
}

export default Profile
