import useKrakenPrices from "../Cryptos/KrakenApi";
import { useEffect, useState } from "react";

const CryptoTable = () => {
    const [cryptos, setCryptos] = useState([]);

    useEffect(() => {
        fetch("http://localhost:8080/api/cryptos") // API от MySQL
            .then(res => res.json())
            .then(data => setCryptos(data));
    }, []);

    const prices = useKrakenPrices(cryptos.map(c => c.symbol));

    return (
        <table>
            <thead>
                <tr>
                    <th>Logo</th>
                    <th>Symbol</th>
                    <th>Name</th>
                    <th>Price (USD)</th>
                </tr>
            </thead>
            <tbody>
                {cryptos.map(coin => (
                    <tr key={coin.symbol}>
                        <td><img src={coin.url} alt={coin.name} width="32" /></td>
                        <td>{coin.symbol}</td>
                        <td>{coin.name}</td>
                        <td>${prices[coin.symbol] || "Loading..."}</td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default CryptoTable;
