import { useEffect, useState } from "react";

export default function useKrakenPricesRest(pairs, refreshInterval = 5000) {
    const [prices, setPrices] = useState({});

    useEffect(() => {
        if (!pairs || pairs.length === 0) return;

        const fetchPrices = () => {
            pairs.forEach(symbol => {
                // Пример: BTC/USD -> BTCUSD
                const krakenSymbol = symbol.replace("/", "");

                fetch(`https://api.kraken.com/0/public/Ticker?pair=${krakenSymbol}`)
                    .then(res => res.json())
                    .then(data => {
                        const pairKey = Object.keys(data.result)[0];
                        if (pairKey) {
                            const lastPrice = data.result[pairKey].c[0];
                            setPrices(prev => ({
                                ...prev,
                                [symbol]: parseFloat(lastPrice).toFixed(2)
                            }));
                        }
                    })
                    .catch(err => console.error(`Error fetching ${symbol}:`, err));
            });
        };

        fetchPrices(); // първо зареждане
        const interval = setInterval(fetchPrices, refreshInterval);

        return () => clearInterval(interval);
    }, [pairs, refreshInterval]);

    return prices;
}
