import { useEffect, useState } from "react";

export default function useKrakenPrices2(pairs) {
    const [prices, setPrices] = useState({});
    const [heartbeatId, setHeartBeatId] = useState();

    const startHeartbeat = (ws, intervalMs = 15000) => {
        return setInterval(() => {
            if (ws.readyState === WebSocket.OPEN) {
                ws.send(JSON.stringify({ method: "ping" }));
                // console.log("ðŸ“¡ Ping sent");
            }
        }, intervalMs);
    }
    const stopHeartbeat = (intervalId) => {
        clearInterval(intervalId);
    }
    const stopConnection = connect(
        "wss://ws.kraken.com/v2",
        (event) => {
            const msg = JSON.parse(event.data);
            console.log("ðŸ“©", msg);
        },
        (ws) => {
            // subscribe ÑÐ»ÐµÐ´ ÑÐ²ÑŠÑ€Ð·Ð²Ð°Ð½Ðµ
            ws.send(JSON.stringify({
                method: "subscribe",
                params: {
                    channel: "ticker",
                    symbol: ["BTC/USD", "ETH/USD"]
                }
            }));
        }
    );

    useEffect(() => {
        const ws = new WebSocket("wss://ws.kraken.com/v2");

        ws.onopen = () => {
            ws.send(JSON.stringify({
                method: "subscribe",
                params: {
                    channel: "ticker",
                    symbol: pairs
                }
            }));
            setHeartBeatId(startHeartbeat(ws, 15000));
        };

        ws.onmessage = (event) => {

            try {
                const msg = JSON.parse(event.data);
                console.log("ðŸ“©", event.data);
                if (msg.channel === "ticker" && (msg.type === "snapshot" || msg.type === "update")) {
                    msg.data.forEach(ticker => {
                        setPrices(prev => ({
                            ...prev,
                            [ticker.symbol]: ticker.last
                        }));
                    });
                }

            } catch (err) {
                console.error("Error:", err);
            }
        };
        ws.onclose = () => {
            stopHeartbeat(heartbeatId);
        }
        return () => ws.close();
    }, [pairs]);

    return prices;
}
