import React, { useState, useEffect } from "react";
import useKrakenWebSocket from "../Cryptos/useKrakenWebSocket"; // Новият hook за live цени
import "./formSellBuy.css";
import "./style.css";

const Profile = ({ onLogout }) => {
    const [user] = useState(JSON.parse(localStorage.getItem("user")));
    const [balance, setBalance] = useState(user?.balance || 0);
    const [holdings, setHoldings] = useState([]);
    const [history, setHistory] = useState([]);
    const [isSellOpen, setIsSellOpen] = useState(false);
    const [selectedCrypto, setSelectedCrypto] = useState(null);
    const [sellQuantity, setSellQuantity] = useState("");
    const [totalSell, setTotalSell] = useState(0);

    const [loadingHoldings, setLoadingHoldings] = useState(true);
    const [loadingHistory, setLoadingHistory] = useState(true);
    const [loadingBalance, setLoadingBalance] = useState(true);

    // Live цени от WebSocket
    const prices = useKrakenWebSocket(holdings.map(h => h.cryptoSymbol));

    // Зареждане на баланс
    const fetchBalance = async () => {
        if (!user?.id) return;
        setLoadingBalance(true);
        try {
            const res = await fetch(`http://localhost:8080/api/users/get/${user.id}`);
            const data = await res.json();
            setBalance(data.balance);
        } catch (err) {
            console.error("Error fetching balance:", err);
        } finally {
            setLoadingBalance(false);
        }
    };

    // Зареждане на holdings
    const fetchHoldings = async () => {
        if (!user?.id) return;
        setLoadingHoldings(true);
        try {
            const res = await fetch(`http://localhost:8080/api/trade/holdings/${user.id}`);
            const data = await res.json();
            setHoldings(data);
        } catch (err) {
            console.error("Error fetching holdings:", err);
        } finally {
            setLoadingHoldings(false);
        }
    };

    // Зареждане на history
    const fetchHistory = async () => {
        if (!user?.id) return;
        setLoadingHistory(true);
        try {
            const res = await fetch(`http://localhost:8080/api/trade/history/${user.id}`);
            const data = await res.json();
            setHistory(data);
        } catch (err) {
            console.error("Error fetching history:", err);
        } finally {
            setLoadingHistory(false);
        }
    };

    // Reset профил
    const resetProfile = async () => {
        if (!user?.id) return;
        if (!window.confirm("Are you sure you want to reset your profile?")) return;

        try {
            const res = await fetch(`http://localhost:8080/api/users/reset/${user.id}`, { method: "PUT" });
            if (!res.ok) throw new Error(await res.text());

            alert("Profile reset successfully");
            fetchBalance();
            fetchHoldings();
            fetchHistory();
        } catch (err) {
            alert(`Error resetting profile: ${err.message}`);
        }
    };

    // Sell crypto
    const makeSell = async () => {
        const qty = parseFloat(sellQuantity);
        if (isNaN(qty) || qty <= 0) return alert("Enter quantity");

        const price = prices[selectedCrypto.cryptoSymbol];
        if (!price) return alert("No price available! Try again later.");

        try {
            const res = await fetch(
                `http://localhost:8080/api/trade/sell/${user.id}?cryptoSymbol=${encodeURIComponent(selectedCrypto.cryptoSymbol)}&quantity=${qty}&price=${price}&cryptoName=${selectedCrypto.cryptoName}`,
                { method: "POST" }
            );

            const message = await res.text();
            if (!res.ok) throw new Error(message);

            alert(message);
            fetchBalance();
            fetchHoldings();
            fetchHistory();
            closeSellForm();
        } catch (err) {
            alert(err.message);
        }
    };

    const openSellForm = (crypto) => {
        setSelectedCrypto(crypto);
        setIsSellOpen(true);
    };

    const closeSellForm = () => {
        setSelectedCrypto(null);
        setIsSellOpen(false);
        setSellQuantity("");
        setTotalSell(0);
    };

    // Първоначално зареждане
    useEffect(() => {
        fetchBalance();
        fetchHoldings();
        fetchHistory();
    }, []);

    return (
        <div className="allProfileInfo">
            <div className="profile">
                <div className="profileInfo">
                    <h1>{user?.name}</h1>
                    <p>{user?.email}</p>
                </div>
                <div className="balance">
                    <h1>
                        Balance: {loadingBalance ? "Loading..." : `${balance.toFixed(2)}$`}
                    </h1>
                </div>
                <div className="profileButtons">
                    <button onClick={resetProfile}>Reset</button>
                    <button onClick={onLogout}>Logout</button>
                </div>
            </div>

            <div className="profileInfo2">
                {/* Holdings */}
                <div className="boxLists">
                    <div className="headerLists"><h1>Holdings</h1></div>
                    <div className="infoLists">
                        {loadingHoldings ? <p>Loading holdings...</p> :
                            holdings.length > 0 ?
                                <table>
                                    <thead>
                                        <tr>
                                            <th>Symbol</th>
                                            <th>Name</th>
                                            <th>Quantity</th>
                                            <th></th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {holdings.map((h, i) => (
                                            <tr key={i}>
                                                <td>{h.cryptoSymbol}</td>
                                                <td>{h.cryptoName}</td>
                                                <td>{h.quantity.toFixed(2)}</td>
                                                <td><button onClick={() => openSellForm(h)}>Sell</button></td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                                : <p>No holdings yet</p>
                        }
                    </div>
                </div>

                {/* History */}
                <div className="boxLists">
                    <div className="headerLists"><h1>History</h1></div>
                    <div className="infoLists">
                        {loadingHistory ? <p>Loading history...</p> :
                            history.length > 0 ?
                                <table>
                                    <thead>
                                        <tr>
                                            <th>Symbol</th>
                                            <th>Name</th>
                                            <th>Type</th>
                                            <th>Quantity</th>
                                            <th>Time</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {history.map((h, i) => (
                                            <tr key={i}>
                                                <td>{h.cryptoSymbol}</td>
                                                <td>{h.cryptoName}</td>
                                                <td>{h.type}</td>
                                                <td>{h.quantity}</td>
                                                <td>{h.timePurchase}</td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                                : <p>No transactions yet</p>
                        }
                    </div>
                </div>

                {/* Sell Form */}
                {isSellOpen &&
                    <div className="overlay">
                        <div className="form-container">
                            <h1>Sell {selectedCrypto.cryptoName}</h1>
                            <p>Symbol: {selectedCrypto.cryptoSymbol}</p>
                            <p>Available: {selectedCrypto.quantity.toFixed(2)}</p>
                            <p>Price: {prices[selectedCrypto.cryptoSymbol] || "Loading..."}</p>
                            <p>Total: {totalSell.toFixed(2)}</p>

                            <input
                                type="number"
                                min="0"
                                step="0.01"
                                max={selectedCrypto.quantity}
                                placeholder="Quantity to sell"
                                value={sellQuantity}
                                onChange={(e) => {
                                    const value = e.target.value;
                                    setSellQuantity(value);
                                    const qty = parseFloat(value);
                                    const price = prices[selectedCrypto.cryptoSymbol];
                                    setTotalSell(!isNaN(qty) && qty > 0 && price ? qty * price : 0);
                                }}
                            />

                            <div className="buttons">
                                <button className="confirm" onClick={makeSell}>Confirm</button>
                                <button className="cancel" onClick={closeSellForm}>Cancel</button>
                            </div>
                        </div>
                    </div>
                }
            </div>
        </div>
    );
};

export default Profile;
