import { useEffect, useState } from "react";
import useKrakenPrices from "../Cryptos/useKrakenPrices";

const CryptoTable = () => {
    const [cryptos, setCryptos] = useState([]);

    // Взимаме криптовалутите от MySQL
    useEffect(() => {
        fetch("http://localhost:8080/api/cryptos")
            .then(res => res.json())
            .then(data => setCryptos(data))
            .catch(err => console.error("Error fetching cryptos:", err));
    }, []);

    // Списък със символи за Kraken
    const prices = useKrakenPrices(cryptos.map(c => c.symbol));

    return (
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
                <tr style={{ background: "#222", color: "#fff" }}>
                    <th style={{ padding: "8px" }}>Logo</th>
                    <th style={{ padding: "8px" }}>Symbol</th>
                    <th style={{ padding: "8px" }}>Name</th>
                    <th style={{ padding: "8px" }}>Price (USD)</th>
                </tr>
            </thead>
            <tbody>
                {cryptos.map(coin => (
                    <tr key={coin.symbol} style={{ borderBottom: "1px solid #444" }}>
                        <td style={{ padding: "8px" }}>
                            <img
                                src={`https://coinicons-api.vercel.app/api/icon/${coin.symbol.split("/")[0].toLowerCase()}`}
                                alt={coin.name}
                                width="28"
                            />
                        </td>
                        <td style={{ padding: "8px" }}>{coin.symbol}</td>
                        <td style={{ padding: "8px" }}>{coin.name}</td>
                        <td style={{ padding: "8px" }}>
                            {prices[coin.symbol] ? `$${prices[coin.symbol]}` : "Loading..."}
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default CryptoTable;
