import { useEffect, useState } from "react";

export default function useKrakenPrices(pairs) {
    const [prices, setPrices] = useState({});

    useEffect(() => {
        if (!pairs || pairs.length === 0) return;

        const ws = new WebSocket("wss://ws.kraken.com/v2");

        ws.onopen = () => {
            console.log("Connected to Kraken WS v2");
            ws.send(JSON.stringify({
                method: "subscribe",
                params: {
                    channel: "ticker",
                    symbol: pairs
                }
            }));
        };

        ws.onmessage = (event) => {
            try {
                const msg = JSON.parse(event.data);

                if (msg.channel === "ticker" && msg.type === "update") {
                    const { symbol, last } = msg.data[0];
                    setPrices(prev => ({
                        ...prev,
                        [symbol]: parseFloat(last).toFixed(2)
                    }));
                }
            } catch (err) {
                console.error("Error parsing Kraken WS:", err);
            }
        };

        ws.onclose = () => console.log("Disconnected from Kraken WS");

        return () => ws.close();
    }, [pairs]);

    return prices;
}
