import { useEffect, useState } from "react";

export default function useTopCryptos() {
    const [cryptos, setCryptos] = useState([]);

    useEffect(() => {
        fetch("https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=20&page=1&sparkline=false")
            .then(res => res.json())
            .then(data => {
                const mapped = data.map(c => ({
                    symbol: c.symbol.toUpperCase() + "/USD",
                    name: c.name,
                    logo: c.image
                }));
                setCryptos(mapped);
            })
            .catch(err => console.error("Error fetching top cryptos:", err));
    }, []);

    return cryptos;
}
