import { useEffect, useState } from "react";

export default function useKrakenPrices(pairs) {
    const [prices, setPrices] = useState({});

    useEffect(() => {
        if (!pairs || pairs.length === 0) return;

        const ws = new WebSocket("wss://ws.kraken.com/v2");

        ws.onopen = () => {
            console.log("Connected to Kraken v2");
            ws.send(JSON.stringify({
                event: "subscribe",
                pair: pairs,
                subscription: { name: "ticker" }
            }));
        };

        ws.onmessage = (event) => {
            try {
                const data = JSON.parse(event.data);

                // Търсим само ticker updates
                if (Array.isArray(data) && data[1]?.c) {
                    const pair = data[3];
                    const price = parseFloat(data[1].c[0]).toFixed(2);
                    setPrices(prev => ({
                        ...prev,
                        [pair]: price
                    }));
                }
            } catch (err) {
                console.error("Error parsing WS message:", err);
            }
        };

        ws.onclose = () => console.log("Disconnected from Kraken");

        return () => ws.close();
    }, [pairs]);

    return prices;
}
