import useKrakenPrices from "../Cryptos/KrackenApi.js";
import useTopCryptos from "../Cryptos/TopCryptos.js";

const HomePage = () => {
    const cryptos = useTopCryptos();
    const prices = useKrakenPrices(cryptos.map(c => c.symbol));

    return (
        <>
            <div className="homeQuote">
                <h1>Practice without risk.<br />Trade with confidence.</h1>
            </div>
            <div>
                <table className="crypto-table">
                    <thead>
                        <tr>
                            <th>Logo</th>
                            <th>Symbol</th>
                            <th>Name</th>
                            <th>Price (USD)</th>
                        </tr>
                    </thead>
                    <tbody>
                        {cryptos.map(coin => (
                            <tr key={coin.symbol}>
                                <td><img src={coin.logo} alt={coin.name} className="crypto-logo" /></td>
                                <td>{coin.symbol}</td>
                                <td>{coin.name}</td>
                                <td>${prices[coin.symbol] || "Loading..."}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </>
    );
};

export default HomePage;
