import { useEffect, useState } from "react";
import useKrakenPrices from "../Cryptos/KrakenApi";

const CryptoTable = () => {
    const [cryptos, setCryptos] = useState([]);

    // Взимаме криптовалутите от MySQL
    useEffect(() => {
        fetch("http://localhost:8080/api/cryptos")
            .then(res => res.json())
            .then(data => setCryptos(data))
            .catch(err => console.error("Error fetching cryptos:", err));
    }, []);

    // Списък със символи за Kraken
    const prices = useKrakenPrices(cryptos.map(c => c.symbol));

    return (
        <table >
            <thead>
                <tr >
                    <th >Logo</th>
                    <th >Symbol</th>
                    <th >Name</th>
                    <th >Price (USD)</th>
                </tr>
            </thead>
            <tbody>
                {cryptos.map(coin => (
                    <tr key={coin.symbol}>
                        <td>
                            <img
                                
                            />
                        </td>
                        <td >{coin.symbol}</td>
                        <td >{coin.name}</td>
                        <td >
                            {prices[coin.symbol] ? `$${prices[coin.symbol]}` : "Loading..."}
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default CryptoTable;
