import { useEffect, useState, useRef } from "react";

export default function useKrakenPrices(pairs) {
    const [prices, setPrices] = useState({});
    const wsRef = useRef(null);
    const heartbeatRef = useRef(null);
    const reconnectRef = useRef(null);
    const isUnmountedRef = useRef(false);

    const startHeartbeat = (ws, intervalMs = 15000) => {
        clearInterval(heartbeatRef.current);
        heartbeatRef.current = setInterval(() => {
            if (ws.readyState === WebSocket.OPEN) {
                ws.send(JSON.stringify({ method: "ping" }));
            }
        }, intervalMs);
    };

    const stopHeartbeat = () => {
        clearInterval(heartbeatRef.current);
    };

    const connect = () => {
        if (isUnmountedRef.current) return;

        const ws = new WebSocket("wss://ws.kraken.com/v2");
        wsRef.current = ws;

        ws.onopen = () => {
            console.log("✅ Connected to Kraken");

            if (!pairs || pairs.length === 0) {
                console.warn("⚠️ No pairs provided, skipping subscribe.");
                return;
            }

            ws.send(JSON.stringify({
                method: "subscribe",
                params: {
                    channel: "ticker",
                    symbol: pairs
                }
            }));

            startHeartbeat(ws);
        };

        ws.onmessage = (event) => {
            try {
                const msg = JSON.parse(event.data);
                console.log("📩 Raw message:", msg);

                if (msg.channel === "ticker" &&
                    (msg.type === "snapshot" || msg.type === "update")) {
                    
                    msg.data.forEach(ticker => {
                        // В Kraken V2 last е масив [price, volume]
                        const price = Array.isArray(ticker.last) ? ticker.last[0] : ticker.last;

                        setPrices(prev => ({
                            ...prev,
                            [ticker.symbol]: price
                        }));
                    });
                }
            } catch (err) {
                console.error("❌ Parse error:", err);
            }
        };

        ws.onerror = () => {
            console.error("⚠️ WebSocket error");
        };

        ws.onclose = (event) => {
            console.warn(`⚠️ Closed: code=${event.code}, reason=${event.reason || "No reason"}`);
            stopHeartbeat();

            if (!isUnmountedRef.current && event.code !== 1000) {
                console.log("🔄 Reconnecting in 3s...");
                reconnectRef.current = setTimeout(connect, 3000);
            }
        };
    };

    useEffect(() => {
        connect();

        return () => {
            isUnmountedRef.current = true;
            stopHeartbeat();
            clearTimeout(reconnectRef.current);
            if (wsRef.current) wsRef.current.close();
        };
    }, [pairs]);

    return prices;
}
