import { useEffect, useState } from "react";

export default function useKrakenPrices(pairs) {
    const [prices, setPrices] = useState({});

    useEffect(() => {

        const ws = new WebSocket("wss://ws.kraken.com/v2");

        ws.onopen = () => {
            ws.send(JSON.stringify({
                method: "subscribe",
                params: {
                    channel: "ticker",
                    symbol: pairs
                }
            }));
        };

        ws.onmessage = (event) => {
            try {
                const msg = JSON.parse(event.data);

                if (msg.channel === "ticker" && msg.type === "snapshot") {
                    msg.data.forEach(ticker => {
                        setPrices(prev => ({
                            ...prev,
                            [ticker.symbol]: ticker.last
                        }));
                    });
                }

                // Kraken Ð¿Ð¾Ð½ÑÐºÐ¾Ð³Ð° Ð¿Ñ€Ð°Ñ‰Ð° update Ð²Ð¼ÐµÑÑ‚Ð¾ snapshot
                if (msg.channel === "ticker" && msg.type === "update") {
                    msg.data.forEach(ticker => {
                        setPrices(prev => ({
                            ...prev,
                            [ticker.symbol]: ticker.last
                        }));
                    });
                }

            } catch (err) {
                console.error("âŒ Error parsing Kraken message:", err);
            }
        };

        ws.onclose = () => console.log("ðŸ”Œ Disconnected from Kraken");

        return () => ws.close();
    }, [pairs]);

    return prices;
}
