import React, {useState} from "react"
import Login from "./LoginSignin/Login.jsx"
import Register from "./LoginSignin/Register.jsx"
import HomePage from "./OtherPages/HomePage";

function App() {
  const [currForm, setCurrForm]=useState("Login");
  const [user, setUser] = useState(null); 
  const toggle = (temp)=>{setCurrForm(temp);}

  return (
    <div>
      {!user ? (currForm === "Login" ? 
        (<Login onFormSwitch={toggle} onLoginSuccess={setUser} />) : 
          (<Register onFormSwitch={toggle} />)) : 
        (<HomePage isLoggedIn={user}/>)}
    </div>
  );
}

export default App;
