import { useEffect, useState } from "react";

export default function useKrakenPrices(pairs) {
    const [prices, setPrices] = useState({});

    useEffect(() => {
        const ws = new WebSocket("wss://ws.kraken.com/v2");

        ws.onopen = () => {
            ws.send(JSON.stringify({
                method: "subscribe",
                params: {
                    channel: "ticker",
                    symbol: pairs
                }
            }));
        };
        ws.heartbeatInterval = setInterval(() => {
            if (ws.readyState === WebSocket.OPEN) {
                ws.send(JSON.stringify({ method: "ping" }));
            }
        }, 20000);
        ws.onmessage = (event) => {
            
            try {
                const msg = JSON.parse(event.data);
                console.log("📩", event.data);
                if (msg.channel === "ticker" && (msg.type === "snapshot"||msg.type === "update")) {
                    msg.data.forEach(ticker => {
                        setPrices(prev => ({
                            ...prev,
                            [ticker.symbol]: ticker.last
                        }));
                    });
                }

            } catch (err) {
                console.error("Error:", err);
            }
        };
        ws.onclose = () => {
        console.log("Връзката е затворена. Опит за повторно свързване след 3 сек...");
        clearInterval(ws.heartbeatInterval);
        setTimeout(connect, 3000); // автоматично свързване след 3 секунди
    };

        return () => ws.close();
    }, [pairs]);

    return prices;
}
