import React, { useState, useEffect } from "react";
import useKrakenPrices from "../Cryptos/KrakenApi";
import "./style.css";

const Profile = ({ onLogout }) => {
    const user = JSON.parse(localStorage.getItem("user"));
    const [holdings, setHoldings] = useState([]);
    const [history, setHistory] = useState([]);
    const [sellData, setSellData] = useState(null); // 游댳 햟향햦 햦햫혟 향햟 혝햣햨혞혤햟혝햟 쮏얧썛웷쐃
    const prices = useKrakenPrices(holdings.map(h => h.cryptoSymbol));

    // 행햟햣햤햢햟햫햣 햫햟 햢햟햫햫햦
    useEffect(() => {
        if (user?.id) {
            fetch(`http://localhost:8080/api/trade/holdings/${user.id}`)
                .then(res => res.json())
                .then(data => setHoldings(data))
                .catch(err => console.error("Error fetching holdings:", err));

            fetch(`http://localhost:8080/api/trade/history/${user.id}`)
                .then(res => res.json())
                .then(data => setHistory(data))
                .catch(err => console.error("Error fetching history:", err));
        }
    }, []);

    // 햊향혥햩햫햣햫햦햣 햫햟 쮏얧썛웷쐃썜햟
    const handleSellConfirm = (quantity) => {
        if (!quantity || quantity <= 0) {
            alert("Please enter a valid quantity");
            return;
        }
        if (quantity > sellData.maxQuantity) {
            alert("You cannot sell more than you have!");
            return;
        }

        fetch("http://localhost:8080/api/trade/sell", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
                userId: user.id,
                symbol: sellData.symbol,
                quantity: parseFloat(quantity),
                price: parseFloat(sellData.price)
            })
        })
            .then(res => res.text())
            .then(msg => {
                alert(msg);
                setSellData(null); // 행햟혝쒫썜혪햪햣 혟쮐햪햟혝햟
                // 햣향햟햣햤햢햟햪햣 햢햟햫햫햦혝햣
                fetch(`http://localhost:8080/api/trade/holdings/${user.id}`)
                    .then(res => res.json())
                    .then(data => setHoldings(data));
                fetch(`http://localhost:8080/api/trade/history/${user.id}`)
                    .then(res => res.json())
                    .then(data => setHistory(data));
            })
            .catch(err => console.error("Sell error:", err));
    };

    return (
        <div className="allProfileInfo">
            <div className="profile">
                <div className="profileInfo">
                    <h1>{user?.name}</h1>
                    <p>{user?.email}</p>
                </div>
                <div className="balance"><h1>Balance: {user?.balance.toFixed(2)}</h1></div>
                <div className="profileButtons">
                    <button>Reset</button>
                    <button onClick={onLogout}>Logout</button>
                </div>
            </div>

            <div className="profileInfo2">
                {/* Holdings */}
                <div className="boxLists">
                    <div className="headerLists"><h1>Holdings</h1></div>
                    <div className="infoLists">
                        {holdings.length > 0 ? (
                            <table>
                                <thead>
                                    <tr>
                                        <th className="symbol">Symbol</th>
                                        <th className="cryptoName">Name</th>
                                        <th className="quantity">Quantity</th>
                                        <th className="sell"></th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {holdings.map((h, index) => (
                                        <tr key={index}>
                                            <td className="symbol">{h.cryptoSymbol}</td>
                                            <td className="cryptoName">{h.cryptoName}</td>
                                            <td className="quantity">{h.quantity}</td>
                                            <td className="sell">
                                                <button onClick={() =>
                                                    setSellData({
                                                        symbol: h.cryptoSymbol,
                                                        name: h.cryptoName,
                                                        maxQuantity: h.quantity,
                                                        price: prices[h.cryptoSymbol] || "Loading..."
                                                    })
                                                }>Sell</button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        ) : <p>No holdings yet</p>}
                    </div>
                </div>

                {/* History */}
                <div className="boxLists">
                    <div className="headerLists"><h1>History</h1></div>
                    <div className="infoLists">
                        {history.length > 0 ? (
                            <table>
                                <thead>
                                    <tr>
                                        <th>Symbol</th>
                                        <th>Name</th>
                                        <th>Type</th>
                                        <th>Quantity</th>
                                        <th>Time</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {history.map((h, index) => (
                                        <tr key={index}>
                                            <td>{h.cryptoSymbol}</td>
                                            <td>{h.cryptoName}</td>
                                            <td>{h.type}</td>
                                            <td>x{h.quantity}</td>
                                            <td>{h.timePurchase}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        ) : <p>No transactions made yet</p>}
                    </div>
                </div>
            </div>

            {/* 햎쮏얧썛 향햟 쮏얧썛웷쐃 */}
            {sellData && (
                <div className="modal-backdrop">
                    <div className="modal">
                        <h2>Sell {sellData.name} ({sellData.symbol})</h2>
                        <p>Available: {sellData.maxQuantity}</p>
                        <p>Current Price: ${sellData.price}</p>
                        <input
                            type="number"
                            min="0"
                            max={sellData.maxQuantity}
                            placeholder="Quantity"
                            onChange={(e) => sellData.inputQuantity = e.target.value}
                        />
                        <div className="modal-buttons">
                            <button onClick={() => handleSellConfirm(sellData.inputQuantity)}>Confirm</button>
                            <button onClick={() => setSellData(null)}>Cancel</button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Profile;
