import { useEffect, useState, useRef, useMemo } from "react";

export default function useKrakenPrices2(pairs) {
    const [prices, setPrices] = useState({});
    const wsRef = useRef(null);
    const reconnectTimeoutRef = useRef(null);
    const heartbeatIntervalRef = useRef(null);
    const retryDelayRef = useRef(3000);
    const isUnmountedRef = useRef(false);

    // ÑÑ‚Ð°Ð±Ð¸Ð»Ð¸Ð·Ð¸Ñ€Ð°Ð¼Ðµ Ð¼Ð°ÑÐ¸Ð²Ð° Ñ useMemo
    const stablePairs = useMemo(() => Array.isArray(pairs) ? pairs : [pairs], [JSON.stringify(pairs)]);

    const startHeartbeat = (ws, intervalMs = 15000) => {
        clearInterval(heartbeatIntervalRef.current);
        heartbeatIntervalRef.current = setInterval(() => {
            if (ws.readyState === WebSocket.OPEN) {
                ws.send(JSON.stringify({ method: "ping" }));
            }
        }, intervalMs);
    };

    const stopHeartbeat = () => {
        clearInterval(heartbeatIntervalRef.current);
        heartbeatIntervalRef.current = null;
    };

    const connect = () => {
        if (isUnmountedRef.current) return;

        // Ñ‡Ð¸ÑÑ‚Ð¸Ð¼ ÐµÐ²ÐµÐ½Ñ‚ÑƒÐ°Ð»ÐµÐ½ ÑÑ‚Ð°Ñ€ reconnect
        clearTimeout(reconnectTimeoutRef.current);

        const ws = new WebSocket("wss://ws.kraken.com/v2");
        wsRef.current = ws;

        ws.onopen = () => {
            console.log("âœ… Connected to Kraken");

            retryDelayRef.current = 3000;

            ws.send(JSON.stringify({
                method: "subscribe",
                params: {
                    channel: "ticker",
                    symbol: stablePairs
                }
            }));

            startHeartbeat(ws, 15000);
        };

        ws.onmessage = (event) => {
            try {
                const msg = JSON.parse(event.data);
                if (msg.channel === "ticker" && (msg.type === "snapshot" || msg.type === "update")) {
                    msg.data.forEach(ticker => {
                        setPrices(prev => ({
                            ...prev,
                            [ticker.symbol]: ticker.last
                        }));
                    });
                }
            } catch (err) {
                console.error("âŒ Parse error:", err);
            }
        };

        ws.onerror = () => {
            console.error("âš ï¸ WebSocket error");
        };

        ws.onclose = (event) => {
            console.warn(`âš ï¸ Closed: code=${event.code}, reason=${event.reason || "No reason"}`);

            stopHeartbeat();

            if (!isUnmountedRef.current && event.code !== 1000) {
                console.log(`ðŸ”„ Reconnecting in ${retryDelayRef.current / 1000}s...`);
                reconnectTimeoutRef.current = setTimeout(connect, retryDelayRef.current);
                retryDelayRef.current = Math.min(retryDelayRef.current * 2, 60000);
            }
        };
    };

    useEffect(() => {
        connect();
        return () => {
            isUnmountedRef.current = true;
            clearTimeout(reconnectTimeoutRef.current);
            stopHeartbeat();
            if (wsRef.current) wsRef.current.close();
        };
    }, [stablePairs]);

    return prices;
}
