import { useEffect, useState } from "react";

export default function useKrakenPrices(pairs) {
    const [prices, setPrices] = useState({});

    const [loading, setLoading] = useState(true);

    useEffect(() => {
        if (!pairs || pairs.length === 0) return;

        setLoading(true); // започваме loading
        const ws = new WebSocket("wss://ws.kraken.com/v2");

        ws.onopen = () => {
            ws.send(JSON.stringify({
                method: "subscribe",
                params: {
                    channel: "ticker",
                    symbol: pairs
                }
            }));
        };

        ws.onmessage = (event) => {
            try {
                const msg = JSON.parse(event.data);
                if (msg.channel === "ticker" && (msg.type === "snapshot" || msg.type === "update")) {
                    setLoading(false); // имаме данни → махаме loading
                    msg.data.forEach(ticker => {
                        setPrices(prev => ({
                            ...prev,
                            [ticker.symbol]: ticker.last
                        }));
                    });
                }
            } catch (err) {
                console.error("Error:", err);
            }
        };

        return () => ws.close();
    }, [pairs]);

    return { prices, loading };
}
