import { useEffect, useState } from "react";
import useKrakenPrices from "../Cryptos/KrakenApi";
import useKrakenPricesRest from "../Cryptos/KrakenApiBugSolve";
import "./styleTable.css"
import "./formSellBuy.css"

const CryptoTable = () => {
    const [cryptos, setCryptos] = useState([]);
    const [isBuyOpen, setIsBuyOpen] = useState(false);
    const savedUser = localStorage.getItem("user");
    const [selectedCrypto, setSelectedCrypto] = useState(null);
    const [buyQuantity, setBuyQuantity] = useState("");
    const [totalBuy, setTotalBuy] = useState(0);
    const prices = useKrakenPricesRest(cryptos.map(c => c.symbol));
    //const prices = useKrakenPrices(cryptos.map(c => c.symbol));

    const openBuyForm = (crypto) => {
        setSelectedCrypto(crypto);
        setIsSellOpen(true);
    }
    const closeBuyForm = () => {
        setSelectedCrypto(null);
        setIsBuyOpen(false);
        setBuyQuantity("");
        setTotalBuy(0);
    }

    const fetchBalance = () => {
        if (!user || !user.id) return;
        fetch(`http://localhost:8080/api/users/get/${user.id}`)
            .then(res => res.json())
            .then(data => setBalance(data.balance))
            .catch(err => console.error("Error fetching balance:", err));
    };

    const fetchData = () => {
        fetchBalance();
        fetch(`http://localhost:8080/api/trade/holdings/${user.id}`)
            .then(res => res.json())
            .then(data => setHoldings(data))
            .catch(err => console.error("Error fetching holdings:", err));

        fetch(`http://localhost:8080/api/trade/history/${user.id}`)
            .then(res => res.json())
            .then(data => setHistory(data))
            .catch(err => console.error("Error fetching history:", err));

    }

    useEffect(() => {
        fetchData();
    }, []);
    return (
        <div>
            <table>
                <thead>
                    <tr>
                        <th className="id">#</th>
                        <th className="logo">Logo</th>
                        <th className="symbol">Symbol</th>
                        <th className="name">Name</th>
                        <th className="price">Price</th>
                        <th className="buy"></th>
                    </tr>
                </thead>
                <tbody>
                    {cryptos.map(coin => (
                        <tr key={coin.symbol}>
                            <td className="id">{coin.id}</td>
                            <td className="logo"><img src={coin.url} /></td>
                            <td className="symbol">{coin.symbol}</td>
                            <td className="name">{coin.name}</td>
                            <td className="price">{prices[coin.symbol] ? `$${prices[coin.symbol]}` : "Loading..."}</td>
                            {
                                savedUser !== null ? <td className="buy"><button onClick={() => openBuyForm(coin)}>Buy</button></td> : <td></td>
                            }
                        </tr>
                    ))}
                </tbody>
            </table>
            {isBuyOpen === true ?

                <div className="overlay">
                    <div className="form-container">
                        <h1>Sell {selectedCrypto.cryptoName}</h1>
                        <p>Symbol: {selectedCrypto.cryptoSymbol}</p>
                        <p>Available: {selectedCrypto.quantity.toFixed(2)}</p>
                        <p>Price: {prices[selectedCrypto.cryptoSymbol]}</p>
                        <p>Total: {totalSell.toFixed(2)}</p>
                        <input type="number" min="0" step="0.01" max={selectedCrypto.quantity}
                            placeholder="Quantity to sell" onChange={(e) => {
                                const value = e.target.value;
                                setSellQuantity(value);

                                const qty = parseFloat(value);
                                const price = prices[selectedCrypto.cryptoSymbol];
                                if (!isNaN(qty) && qty > 0 && price) {
                                    setTotalSell(qty * price);
                                } else {
                                    setTotalSell(0);
                                }
                            }} />

                        <div className="buttons">
                            <button className="confirm" onClick={() => makeBuy()}>Confirm</button>
                            <button className="cancel" onClick={() => closeBuyForm()}>Cancel</button>
                        </div>
                    </div>
                </div> : <></>};
        </div>
    );
};

export default CryptoTable;
