import "./style.css";
import useKrakenPrices from "../KrakenApi.js";
//import "./CryptoTable.css";

const CRYPTOS = [
    { symbol: "BTC/USD", name: "Bitcoin", logo: "https://cryptologos.cc/logos/bitcoin-btc-logo.svg?v=025" },
    { symbol: "ETH/USD", name: "Ethereum", logo: "https://cryptologos.cc/logos/ethereum-eth-logo.svg?v=025" },
    { symbol: "XRP/USD", name: "XRP", logo: "https://cryptologos.cc/logos/xrp-xrp-logo.svg?v=025" },
    { symbol: "ADA/USD", name: "Cardano", logo: "https://cryptologos.cc/logos/cardano-ada-logo.svg?v=025" }
];

const HomePage = () => {
    const prices = useKrakenPrices(CRYPTOS.map(c => c.symbol));

    return (
        <>
            <div className="homeQuote">
                <h1>Practice without risk.<br />Trade with confidence.</h1>
            </div>
            <div>
                <table className="crypto-table">
                    <thead>
                        <tr>
                            <th>Logo</th>
                            <th>Symbol</th>
                            <th>Name</th>
                            <th>Price (USD)</th>
                        </tr>
                    </thead>
                    <tbody>
                        {CRYPTOS.map(coin => (
                            <tr key={coin.symbol}>
                                <td><img src={coin.logo} alt={coin.name} className="crypto-logo" /></td>
                                <td>{coin.symbol}</td>
                                <td>{coin.name}</td>
                                <td>${prices[coin.symbol] || "Loading..."}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </>
    );
};

export default HomePage;
