import React, {useState} from "react"
import "./OtherPages/style.css"
import Login from "./LoginSignin/Login.jsx"
import Register from "./LoginSignin/Register.jsx"
import HomePage from "./OtherPages/HomePage.jsx";
import Profile from "./OtherPages/Profile.jsx";

function App() {
  const [currForm, setCurrForm]=useState("Home");
  const [user, setUser] = useState(null); 
  const toggle = (temp)=>{setCurrForm(temp);}

  const handleLoginSuccess = () => {
    localStorage.setItem("user", JSON.stringify(loggedUser));
    setCurrForm("Home");
  };

  checkUser(() => {
    const savedUser = localStorage.getItem("user");
    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    window.location.reload();
    setUser(null);
    setCurrForm("Home");
  };

  return (
     <div>
      <header>
        <a onClick={()=>setCurrForm("Home")}>TradingSimulation</a>
        <nav>
          {user ? (<a onClick={() => setCurrForm("Profile")}>Profile</a>) :
          (<a onClick={() => setCurrForm("Login")}>Log In</a>)}
        </nav>
      </header>

      {currForm === "Home" && <HomePage />}

      {currForm === "Profile" && <Profile onLogout={handleLogout} />}

      {!user && currForm === "Login" && 
      (<Login onFormSwitch={toggle} onLoginSuccess={handleLoginSuccess} />)}

      {!user && currForm === "Register" && (<Register onFormSwitch={toggle} />)}
    </div>
  );
}

export default App;
