import { useEffect, useRef, useState } from "react";

export default function useKrakenPrices(symbols) {
    const [prices, setPrices] = useState({});
    const wsRef = useRef(null);

    useEffect(() => {
        if (!wsRef.current) {
            const ws = new WebSocket("wss://ws.kraken.com/v2");
            wsRef.current = ws;

            ws.onopen = () => {
                console.log("‚úÖ Connected to Kraken");
                if (symbols.length) {
                    ws.send(JSON.stringify({
                        method: "subscribe",
                        params: { channel: "ticker", symbol: symbols }
                    }));
                }
            };

            ws.onmessage = (event) => {
                try {
                    const msg = JSON.parse(event.data);
                    if (msg.channel === "ticker" && msg.data?.length) {
                        const s = msg.data[0].symbol;
                        const last = msg.data[0].last;
                        setPrices(prev => ({ ...prev, [s]: parseFloat(last) }));
                    }
                } catch {}
            };

            ws.onclose = () => console.warn("‚ùå Disconnected from Kraken");
            ws.onerror = (err) => console.error("WS Error:", err);
        }

        // üìå –ê–∫–æ –≤–µ—á–µ –∏–º–∞–º–µ –≤—Ä—ä–∑–∫–∞, –∞ —Å–∏–º–≤–æ–ª–∏—Ç–µ —Å–µ –ø—Ä–æ–º–µ–Ω—è—Ç ‚Üí –ø—Ä–µ–∑–∞–ø–∏—Å–≤–∞–º–µ –∞–±–æ–Ω–∞–º–µ–Ω—Ç–∞
        if (wsRef.current?.readyState === WebSocket.OPEN && symbols.length) {
            wsRef.current.send(JSON.stringify({
                method: "subscribe",
                params: { channel: "ticker", symbol: symbols }
            }));
        }
    }, [symbols]);

    return prices;
}
