import React, { useState } from "react";
import "./SellForm.css";

export default function SellForm({ userId, symbol, name, maxQuantity, price, onClose, onSellSuccess }) {
    const [quantity, setQuantity] = useState("");

    const handleConfirm = () => {
        if (!quantity || quantity <= 0) {
            alert("Please enter a valid quantity");
            return;
        }
        if (quantity > maxQuantity) {
            alert("You cannot sell more than you have!");
            return;
        }

        fetch("http://localhost:8080/api/trade/sell", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
                userId,
                symbol,
                quantity: parseFloat(quantity),
                price: parseFloat(price)
            })
        })
            .then(res => res.text())
            .then(msg => {
                alert(msg);
                onSellSuccess(); // обновява holdings и history
                onClose();       // затваря popup-а
            })
            .catch(err => console.error("Sell error:", err));
    };

    return (
        <div className="modal-backdrop">
            <div className="modal">
                <h2>Sell {name} ({symbol})</h2>
                <p>Available: {maxQuantity}</p>
                <p>Current Price: ${price}</p>
                <input
                    type="number"
                    min="0"
                    max={maxQuantity}
                    placeholder="Quantity"
                    value={quantity}
                    onChange={(e) => setQuantity(e.target.value)}
                />
                <div className="modal-buttons">
                    <button onClick={handleConfirm}>Confirm</button>
                    <button onClick={onClose}>Cancel</button>
                </div>
            </div>
        </div>
    );
}
