import { useEffect, useState } from "react";
import useKrakenPrices from "../Cryptos/KrakenApi";
import useKrakenPricesRest from "../Cryptos/KrakenApiBugSolve";
import "./styleTable.css"

const CryptoTable = () => {
    const [cryptos, setCryptos] = useState([]);

    useEffect(() => {
        fetch("http://localhost:8080/api/cryptos")
            .then(res => res.json())
            .then(data => setCryptos(data))
            .catch(err => console.error("Error data:", err));
    }, []);
    //const prices = useKrakenPrices(cryptos.map(c => c.symbol));
    const prices = useKrakenPricesRest(cryptos.map(c => c.symbol));
    return (
        <table>
            <thead>
                <tr>
                    <th>#</th>
                    <th>Logo</th>
                    <th>Symbol</th>
                    <th>Name</th>
                    <th>Price</th>
                </tr>
            </thead>
            <tbody>
                {cryptos.map(coin => (
                    <tr key={coin.symbol}>
                        <td>{coin.id}</td>
                        <td><img src={coin.url} /></td>
                        <td>{coin.symbol}</td>
                        <td>{coin.name}</td>
                        <td>{prices[coin.symbol] ? `$${prices[coin.symbol]}` : "Loading..."}</td>
                        <td>Buy</td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default CryptoTable;
