import React, {useState} from "react"
import "./OtherPages/style.css"
import Login from "./LoginSignin/Login.jsx"
import Register from "./LoginSignin/Register.jsx"
import HomePage from "./OtherPages/HomePage";

function App() {
  const [currForm, setCurrForm]=useState("Login");
  const [user, setUser] = useState(null); 
  const toggle = (temp)=>{setCurrForm(temp);}

  return (
     <div>
      <header>
        <a href="#">TradingSimulation</a>
        <nav>
          {user ? (
            <>
              <a onClick={() => setCurrForm("Profile")}>Profile</a>
            </>
          ) : (
            <>
              <button onClick={() => setCurrForm("Login")}>Log In</button>
              <button onClick={() => setCurrForm("Register")}>Register</button>
            </>
          )}
        </nav>
      </header>

      {/* CONTENT */}
      {currForm === "Home" && <HomePage />}
      {!user && currForm === "Login" && (
        <Login onFormSwitch={toggleForm} onLoginSuccess={setUser} />
      )}
      {!user && currForm === "Register" && (
        <Register onFormSwitch={toggleForm} />
      )}
    </div>
  );
}

export default App;
