import { useEffect, useRef, useState } from "react";

export default function useKrakenPrices(pairs) {
    const [prices, setPrices] = useState({});
    const wsRef = useRef(null);
    const reconnectTimeout = useRef(null);

    const connect = () => {
        const ws = new WebSocket("wss://ws.kraken.com/v2");
        wsRef.current = ws;

        ws.onopen = () => {
            console.log("Connected to Kraken WebSocket");
            ws.send(JSON.stringify({
                method: "subscribe",
                params: {
                    channel: "ticker",
                    symbol: pairs
                }
            }));
        };

        ws.onmessage = (event) => {
            try {
                const msg = JSON.parse(event.data);

                if (msg.channel === "ticker" && (msg.type === "snapshot" || msg.type === "update")) {
                    msg.data.forEach(ticker => {
                        setPrices(prev => ({
                            ...prev,
                            [ticker.symbol]: ticker.last
                        }));
                    });
                } else if (msg.channel === "heartbeat") {
                    // heartbeat → връзката е жива
                }
            } catch (err) {
                console.error("Error parsing message:", err);
            }
        };

        ws.onerror = (err) => {
            console.error("WebSocket error:", err);
            ws.close(); // ще влезе в onclose()
        };

        ws.onclose = () => {
            console.warn("WebSocket closed, reconnecting in 5s...");
            reconnectTimeout.current = setTimeout(connect, 5000);
        };
    };

    useEffect(() => {
        connect();
        return () => {
            if (wsRef.current) wsRef.current.close();
            if (reconnectTimeout.current) clearTimeout(reconnectTimeout.current);
        };
    }, [pairs]);

    return prices;
}
