import React, { useEffect, useState } from "react";
import useKrakenPrices from "../Cryptos/KrakenApi.jsx";

export default function CryptoTable() {
    const [cryptos, setCryptos] = useState([]);
    
    useEffect(() => {
        fetch("http://localhost:8080/api/cryptos")
            .then(res => res.json())
            .then(data => setCryptos(data))
            .catch(err => console.error("Error loading cryptos:", err));
    }, []);

    const prices = useKrakenPrices(cryptos.map(c => c.symbol));

    return (
        <table className="crypto-table">
            <thead>
                <tr>
                    <th>Logo</th>
                    <th>Symbol</th>
                    <th>Name</th>
                    <th>Price (USD)</th>
                </tr>
            </thead>
            <tbody>
                {cryptos.map(c => (
                    <tr key={c.symbol}>
                        <td><img src={c.url} alt={c.name} className="crypto-logo" /></td>
                        <td>{c.symbol}</td>
                        <td>{c.name}</td>
                        <td>{prices[c.symbol] || "Loading..."}</td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
}
